def symbol_to_coin_id(dataframe, symbol):
	for i in range(0,len(dataframe)):
		if str(symbol).lower() == 'usdc': 
			symbol = "usd-coin"
		if str(symbol).lower() == 'btc':
			symbol = 'bitcoin'
		if str(symbol).lower() == str(dataframe["symbol"][i]):
			symbol = dataframe["id"][i]
	return symbol



